using Domain.Contracts;

namespace Domain.Utils;

public sealed class HtmlUtils
{
    public static string CreatePreviewForFreeTime(BuildOpenGraphImage preview, string perfilBase64)
    {
        var tagPrepared = "";
        if (preview.Tags?.Count > 0)
        {
            foreach (var item in preview.Tags)
            {
                tagPrepared +=
                    $@"<span style='background-color: #DFDBFF; color: #2E2E2E; padding: 2px; text-align: center; border-radius: 5px; overflow: hidden; height: 24px; text-overflow: ellipsis; max-width: 48%; white-space: nowrap;'>
                            {item.Titulo}
                        </span>";
            }
        }
        var html =
            $@" @import url('https://fonts.googleapis.com/css2?family=League+Spartan:wght@300;400;500&family=Open+Sans:wght@300&display=swap');
            <div id='free-time-preview' style='display: flex;flex-direction: column; width: 400px; height: 245px; padding: 20px 30px 10px; font-family: 'League Spartan', sans-serif;background-color: #F5F5F5; justify-content: space-between;'>
                <div style='display: flex; width: fit-content; justify-content: space-between;'>
                    <div style='display: flex; flex-direction: row;'>
                        <div style='display: flex; padding-bottom: 20px; gap: 12px; color: #424C56; justify-content: center'>
                           <div style=' display: block; font-size: 32px;'>
                                <img src='data:image/png;base64,{perfilBase64}' style='object-fit: cover; width: 50px; height: 50px; border-radius: 50%;' />
                            </div>
                            <div style='display: block; font-size: 24px;'>
                                {preview.TempTitle}
                                <div style='max-width: fit-content; padding: 3px;padding-left: 5px;padding-right: 5px; border-radius: 5px;margin: 2px; margin-top: 4px;overflow: hidden;height: 15px;text-overflow: ellipsis;font-size: 18px; font-weight: bold'>
                                    {preview.UserName}
                                </div>
                            </div>
                            <strong style='text-align: center;'>60 minutos</strong>
                        </div>
                    </div>
                </div>                   
                <div style='margin-bottom: 15px; display: flex; flex-wrap: wrap; overflow: hidden; gap: 4px'>
                    {tagPrepared}
                </div>
                <div style='display: flex; align-items: center; width: 100%; margin-bottom: 3px; justify-content: space-between;'>
                    <div style='display: flex; align-items: center; font-size: 22px; color: #424C56; gap: 115px;'>
                        <div style='text-align: center'>{preview.StartTime:dd/MM/yyyy} {preview.StartTime:HH:mm} - {preview.EndTime:HH:mm}</div>
                        <img src='data:image/png;base64,' 
                            style=' width: 30px; height: 30px;' 
                        />                
                    </div>
                </div>
            </div>
        ";

        return html;
    }
}
